var debug, handleCaptureMessage;

debug = require('../utils').debug;

handleCaptureMessage = function(econ, message) {
  var matches;
  if (matches = /^\[game\]: flag_capture player='([0-9]+):(.+?)'$/.exec(message)) {
    debug.events('%s:%s econ %s event', econ.server.host, econ.server.port, 'capture');
    return econ.emit('capture', {
      player: matches[2],
      client: econ.getClientInfo(parseInt(matches[1]))
    });
  }
};

module.exports = handleCaptureMessage;
