var debug, handleLeaveMessage;

debug = require('../utils').debug;

handleLeaveMessage = function(econ, message) {
  var matches;
  if (matches = /^\[game\]: leave player='([0-9]+):(.+?)'$/.exec(message)) {
    debug.events('%s:%s econ %s event', econ.server.host, econ.server.port, 'leave');
    return econ.emit('leave', {
      player: matches[2],
      client: econ.getClientInfo(parseInt(matches[1]))
    });
  }
};

module.exports = handleLeaveMessage;
